package io.github.brenovambaster.calc;

import io.github.brenovambaster.calc.models.Ponto;

import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;

import java.io.IOException;

@WebServlet(name = "pontoServlet", value = "/ponto")
public class PontoServlet extends HttpServlet {
    @Override
    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        Ponto pontoA = new Ponto();
        Ponto pontoB = new Ponto();

        String coordenadaAx = req.getParameter("coordenadaAx");
        String coordenadaAy = req.getParameter("coordenadaAy");
        String coordenadaBx = req.getParameter("coordenadaBx");
        String coordenadaBy = req.getParameter("coordenadaBy");

        // repassa valores para a JSP
        req.setAttribute("coordenadaAx", coordenadaAx);
        req.setAttribute("coordenadaAy", coordenadaAy);
        req.setAttribute("coordenadaBx", coordenadaBx);
        req.setAttribute("coordenadaBy", coordenadaBy);

        try {
            pontoA.setX(Double.parseDouble(coordenadaAx));
            pontoA.setY(Double.parseDouble(coordenadaAy));

            pontoB.setX(Double.parseDouble(coordenadaBx));
            pontoB.setY(Double.parseDouble(coordenadaBy));

            double distancia = pontoA.distancia(pontoB);

            req.setAttribute("distancia", distancia);

        } catch (NumberFormatException e) {
            req.setAttribute("error", "Entrada inválida: verifique as coordenadas.");
        }

        req.getRequestDispatcher("/ponto.jsp").forward(req, resp);

    }

    @Override
    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        req.getRequestDispatcher("/ponto.jsp").forward(req, resp);
    }

}

